import React, { Component } from 'react';
import {runQuery } from './helpers/runQuery';
import './App.css';


class App extends Component {

  state = {
    value: []
  }  

  componentDidMount(){
    const url ="https://api.data.netwerkdigitaalerfgoed.nl/datasets/ivo/NMVW/services/NMVW-39/sparql"
    //Note that the query is wrapped in es6 template strings to allow for easy copy pasting
    const query = `
    PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
    PREFIX dc: <http://purl.org/dc/elements/1.1/>
    PREFIX dct: <http://purl.org/dc/terms/>
    PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
    PREFIX edm: <http://www.europeana.eu/schemas/edm/>
    PREFIX foaf: <http://xmlns.com/foaf/0.1/>

    SELECT ?cho ?title ?typeLabel WHERE {

      <https://hdl.handle.net/20.500.11840/termmaster1397> skos:narrower* ?type .
      ?type skos:prefLabel ?typeLabel .

      ?cho edm:object ?type .
      ?cho dc:title ?title .
      FILTER langMatches(lang(?title), "ned")
    } LIMIT 10

    `
    runQuery(url, query)
    .then(json => {

    const result = json.results.bindings;

    this.setState({
      value: result
    })


    })

  }




  render(){
    return (
      <div className="App">

        {this.state.value.map((item, index)=>(
          <p key={index}>{item.title.value}</p>
        ))}
      </div>
    );
  }
}

export default App;
