import React, { Component } from 'react'
import 'canvas-free-drawing';

class DrawingBoard extends Component {

    componentDidMount(){
        const cfd = new CanvasFreeDrawing({
            elementId: 'cfd',
            width: 500,
            height: 500,
        });
    }


    render() {
        return (
            <div>
                dit is een tekenveld
                <canvas id="cfd"></canvas>
            </div>
        )
    }
}



export default DrawingBoard;
