import React, { Component } from 'react'
import CanvasFreeDrawing from 'canvas-free-drawing';

class DrawingBoard extends Component {

    state = {

    }

    componentDidMount(){
        const cfd = new CanvasFreeDrawing({
            elementId: 'cfd',
            width: 500,
            height: 500,
        });
        let i = 0;
          // set properties
        cfd.setLineWidth(10); // in px
        cfd.setStrokeColor([i, i, i]); // in RGB

        // listen to events
        cfd.on({ event: 'redraw' }, () => {
            // code...
            if (i < 255){
                i++;
            }
        cfd.setStrokeColor([i, i, i]); // in RGB

            console.log(i);
        });

    }


    render() {
        return (
            <div>
                <canvas id="cfd"></canvas>
            </div>
        )
    }
}



export default DrawingBoard;
