import React from 'react'
import CanvasFreeDrawing from 'canvas-free-drawing';


export default function CanvasBoard() {
    const cfd = new CanvasFreeDrawing({
        elementId: 'cfd',
        width: 500,
        height: 500,
    });

    this.cfd.setLineWidth(10); // in px
    this.cfd.setStrokeColor([255,0,0] ); // in RGB





    return (
        <div>
                <canvas id="cfd"></canvas>
                <button onClick={() => changeColor('white')}>Gum</button>
                <button onClick={() => changeColor('black')}>Zwart</button>
                <button onClick={() => changeColor('gray')}>Grijs</button>
                <button onClick={() => changeColor('red')}>Rood</button>
                <button onClick={() => changeColor('blue')}>Blauw</button>
                <button onClick={() => changeColor('purple')}>Paars</button>
                <button onClick={() => changeColor('green')}>Groen</button>
                <button onClick={() => changeColor('brown')}>Bruin</button>
                <button onClick={() => changeColor('orange')}>Oranje</button>
                <button onClick={() => changeColor('yellow')}>Geel</button>
        </div>
    )
}

function changeColor(color){
        console.log('hallo' + color);
        this.cfd.setStrokeColor([100,100,100]);
